function findMax(str) {
  var map = {},
    max = { num: 0 };

  for (var i in str) {
    if (map[str[i]]) {
      map[str[i]]++;
    } else {
      map[str[i]] = 1;
    }

    if (map[str[i]] > max.num) {
      max.num = map[str[i]];
      max.key = str[i];
    }
  }
