function PromiseM() {
  this.status = "pending";
  this.msg = "";
  var that = this;
  var process = arguments[0];
  process(function() {
    that.status = 'resolve';
    that.msg =arguments[0];
  }, function() {
    that.status = 'reject';
    that.msg =arguments[0];
  });
  return this;
}
PromiseM.prototype.then = function() {
  if(this.status == 'resolve') {
    arguments[0](this.msg);
  }
  if(this.status == 'reject' && arguments[1]) {
    arguments[1](this.msg);
  }
}

// 测试用例

var p = new PromiseM(function(resolve, reject) {
  resolve("123");
});

p.then(
  function(success) {
    console.log(success);
    console.log("success");
  },
  function() {
    console.log("fail！");
  }
);

