    resolve();
  }).then(function() {
    console.log("4");
  });
});

new Promise(function(resolve) {
  console.log("5");
  resolve();
}).then(function() {
  console.log("6");
});

setTimeout(function() {
  console.log("7");

  new Promise(function(resolve) {
    console.log("8");
    resolve();
  }).then(function() {
    console.log("9");
  });
});

